(function (win, doc, storage) {
  var body, fakeBody, div, span, overflow,
    key        = "dr-webfonts",
    url        = "/assets/css/006/fonts-{{format}}.css";
    html       = doc.documentElement,
    style      = "&#173;<style>@font-face{font-family:test-woff;src:url(\"data:application/x-font-woff;base64,\") format(\"woff\");font-weight:400}@font-face{font-family:test-ttf;src:url(\"data:application/x-font-ttf;base64,\") format(\"truetype\");font-weight:400}@font-face{font-family:test-svg;src:url(\"data:image/svg+xml;base64,\") format(\"svg\");font-weight:400}.test-woff,.test-ttf,.test-svg{font-size:10px;font-weight:400}.test-woff{font-family:test-woff}.test-ttf{font-family:test-ttf}.test-svg{font-family:test-svg}</style>",
    formats    = ["woff", "ttf", "svg"],
    isComplete = false,
    isRunning  = false,
    callbacks  = [],
    support    = {};

  if (win.DR != null) {
    if (win.DR.basePath != null) {
      url = win.DR.basePath + url;
    }
    if (win.DR.proxyUrl != null) {
      url = win.DR.proxyUrl + url;
    }
  }

  if ((typeof(storage) !== "undefined") && (storage.getItem(key) != null)) {
    fontSupport(function (supported) {
      if (supported) {
        request(url.replace("{{format}}", supported), store);
      }
      else {
        try {
          storage.setItem(key, "no formats supported");
        } catch (e) {
          console.log("Could not save local storage data: no formats supported", e);
        }
      }
    }, ["woff", "ttf", "svg"]);
  }

  function fontSupport(callback, returnOption) {
    if (isComplete) {
      handleCallbacks();
    } else {
      callbacks.push({callback: callback, option: returnOption || null});
      if (isRunning) {
        return;
      }
      detect();
    }
  };
  
  function formatResult (option) {
    var result;
    if (!option) {
      result = support;
    }
    else if (typeof option == "string") {
      result = support[option];
    }
    else {
      result = option.filter(function (format) {
        return support[format];
      });
      result = 0 in result && result[0] || null;
    }
    return result;
  }
  
  function handleCallbacks () {
    var data;
    while (callbacks.length) {
      data = callbacks.shift();
      data.callback(formatResult(data.option));
    }
  }
  
  function detect () {
    isRunning = true;
    body = doc.body,
    fakeBody = body || doc.createElement("body"),
    div = doc.createElement("div"),
    span = doc.createElement("span"),

    span.innerHTML = ".";

    (body ? div : fakeBody).innerHTML += style;
    fakeBody.appendChild(div);
    if (!body) {
      fakeBody.style.background = "";
      fakeBody.style.overflow = "hidden";
      overflow = html.style.overflow;
      html.style.overflow = "hidden";
      html.appendChild(fakeBody);
    }

    var i = 0,
      l = formats.length,
      countdown = l,
      clones = [],
      detected,
      start = new Date(),
      timeout = 2000;

    while (i < l) {
      (function (format, clone) {
        clone.className = "test-" + format;
        clones.push(clone);
        div.appendChild(clone);
      }(formats[i++], span.cloneNode(true)));
    }

    (function check() {
      var i = 0;
      while (i < l) {
        if (clones[i].offsetWidth == 10) {
          detected = true;
          support[formats[i]] = true;
        }
        i++;
      }
      if (detected) {
        isComplete = true;
        cleanup();
      }
      else if (new Date() - start > timeout) {
        cleanup();
      }
      else {
        setTimeout(check, 100);
      }
    }());
  }
  
  function cleanup() {
    if (!body) {
      fakeBody.parentNode.removeChild(fakeBody);
      html.style.overflow = overflow;
    } else {
      div.parentNode.removeChild(div);
    }
    handleCallbacks();
    isRunning = false;
  }

  function store(css) {
    if (css.indexOf("@font-face") == 0) {
      try {
        storage.setItem(key, css);
      } catch (e) {
        console.log("Could not save local storage data: " + key, e);
      }
      var head = doc.head || doc.getElementsByTagName("head")[0],
        style = doc.createElement("style");
      style.textContent = css;
      head.appendChild(style);
    }
  }

  function request(url, callback) {
    var xhr = new XMLHttpRequest();

    xhr.open("GET", url, true);
    xhr.onload = function () {
      callback(xhr.responseText);
    };
    xhr.send(null);
  }

}(window, window.document, window.localStorage));
